// Legacy Footer Component (kept for backward compatibility, but use PDFFooter without timestamp)
interface PDFFooterLegacyProps {
  pageNumber?: number
  totalPages?: number
  creationTimestamp?: string
}

export const PDFFooterLegacy: React.FC<PDFFooterLegacyProps> = ({
  pageNumber,
  totalPages,
  creationTimestamp
}) => {
  const timestamp = creationTimestamp || new Date().toLocaleString('en-US', {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit'
  })

  return (
    <View style={PDFStyles.footer}>
      <Text style={PDFStyles.footerLeft}>
        This document is part of Comfrut's quality management system.
      </Text>
      <Text style={PDFStyles.footerRight}>
        {pageNumber && totalPages ? `Page ${pageNumber} of ${totalPages}` : ''}
        {pageNumber && totalPages && creationTimestamp ? '  ' : ''}
        {creationTimestamp ? timestamp : ''}
      </Text>
    </View>
  )
}

