// Status Badge Component
interface PDFStatusBadgeProps {
  status: 'comply' | 'notComply' | 'withinRange' | 'overLimit' | 'underLimit' | 'observation' | 'pending'
  customText?: string
}

export const PDFStatusBadge: React.FC<PDFStatusBadgeProps> = ({ status, customText }) => {
  const statusConfig = PDFStatusColors[status]
  
  return (
    <View style={[PDFStyles.statusBadge, { backgroundColor: statusConfig.backgroundColor }]}>
      <Text style={{ color: statusConfig.color, fontSize: 8, fontWeight: 'bold', textAlign: 'center' }}>
        {customText || statusConfig.text}
      </Text>
    </View>
  )
}

// Two-Row Header Component
interface PDFHeader2RowProps {
  titleEn: string
  titleEs?: string
  documentCode: string
  version?: string
}

export const PDFHeader2Row: React.FC<PDFHeader2RowProps> = ({
  titleEn,
  titleEs,
  documentCode,
  version = 'V.01'
}) => {
  const titleLength = titleEn.length
  const titleFontSize = getTitleFontSize(titleLength)

  return (
    <View style={{ marginBottom: 15, paddingBottom: 12, borderBottomWidth: 1, borderBottomColor: '#E5E7EB', minHeight: PDFTokens.page.headerHeight }}>
      {/* Row A: Logo, Title, Code + Version */}
      <View style={PDFStyles.headerRowA}>
        {/* Left: Logo */}
        <View style={PDFStyles.headerLeft}>
          <Image src={logoBase64} style={PDFStyles.logo} />
        </View>

        {/* Center: Document Title */}
        <View style={PDFStyles.headerCenter}>
          <Text 
            style={[PDFStyles.headerTitleEnglish, { fontSize: titleFontSize }]}
            wrap={true}
          >
            {titleEn}
          </Text>
          {titleEs && (
            <Text style={PDFStyles.headerTitleSpanish} wrap={true}>{titleEs}</Text>
          )}
        </View>

        {/* Right: Document Code + Version */}
        <View style={PDFStyles.headerRight}>
          <Text style={PDFStyles.documentCode}>{documentCode}</Text>
          <Text style={PDFStyles.documentVersion}>{version}</Text>
        </View>
      </View>
    </View>
  )
}

