// Photo Grid Component (1 or 2 photos per page)
interface PDFPhotoGridProps {
  photos: Array<{ url: string; caption?: string }>
  photosPerPage?: 1 | 2
}

export const PDFPhotoGrid: React.FC<PDFPhotoGridProps> = ({
  photos,
  photosPerPage = 1
}) => {
  if (photosPerPage === 1) {
    return (
      <>
        {photos.map((photo, index) => (
          <PDFPhotoCard
            key={index}
            photoUrl={photo.url}
            photoNumber={index + 1}
            caption={photo.caption}
          />
        ))}
      </>
    )
  }

  // 2 photos per page layout
  const photoPairs: Array<Array<{ url: string; caption?: string }>> = []
  for (let i = 0; i < photos.length; i += 2) {
    photoPairs.push(photos.slice(i, i + 2))
  }

  return (
    <>
      {photoPairs.map((pair, pairIndex) => (
        <View key={pairIndex} style={{ flexDirection: 'row', justifyContent: 'space-between', marginBottom: 15 }}>
          {pair.map((photo, photoIndex) => (
            <View key={photoIndex} style={{ width: '48%' }}>
              <PDFPhotoCard
                photoUrl={photo.url}
                photoNumber={pairIndex * 2 + photoIndex + 1}
                caption={photo.caption}
              />
            </View>
          ))}
        </View>
      ))}
    </>
  )
}

